import React, { useState } from 'react';

function AuthPage({ onAuthSuccess }) {
  const [isLogin, setIsLogin] = useState(true); // Toggle between login and register
  const [identifier, setIdentifier] = useState(''); // Supports both username and email
  const [email, setEmail] = useState('');
  const [user_psw, setUserPsw] = useState(''); // Consistent with backend field
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');

  const toggleForm = () => {
    setIsLogin(!isLogin);
    setError('');
    setIdentifier('');
    setEmail('');
    setUserPsw('');
    setConfirmPassword('');
  };

  const handleAuth = async (e) => {
    e.preventDefault();
    setError('');

    if (!isLogin && user_psw !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    try {
      // Define endpoint and payload
      const endpoint = isLogin ? '/api/User/login' : '/api/User';
      const payload = isLogin
        ? { identifier, user_psw } // Login payload
        : { username: identifier, email, user_psw }; // Registration payload

      // Make the request
      const response = await fetch(`http://localhost:5000${endpoint}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      if (!response.ok) {
        const errorText = await response.text(); // Get error response
        throw new Error(errorText || (isLogin ? 'Login failed' : 'Registration failed'));
      }

      const data = await response.json();

      if (!isLogin) {
        console.log(`Registration successful. UserId: ${data.userId}`);
      }

      // Handle successful authentication
      onAuthSuccess();
    } catch (err) {
      setError(err.message || 'Something went wrong');
    }
  };

  return (
    <div>
      <h2>{isLogin ? 'Login' : 'Register'}</h2>
      <form onSubmit={handleAuth}>
        <input
          type="text"
          placeholder={isLogin ? 'Username or Email' : 'Username'}
          value={identifier}
          onChange={(e) => setIdentifier(e.target.value)}
          required
        />
        {!isLogin && (
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        )}
        <input
          type="password"
          placeholder="Password"
          value={user_psw}
          onChange={(e) => setUserPsw(e.target.value)}
          required
        />
        {!isLogin && (
          <input
            type="password"
            placeholder="Confirm Password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
        )}
        <button type="submit">{isLogin ? 'Login' : 'Register'}</button>
        {error && <p style={{ color: 'red' }}>{error}</p>}
      </form>
      <button onClick={toggleForm}>
        {isLogin ? 'Switch to Register' : 'Switch to Login'}
      </button>
    </div>
  );
}

export default AuthPage;
