import React, { useState, useEffect } from "react";
import "./MovieDetail.css";

const MovieDetail = ({ movieId }) => {
    const [movieData, setMovieData] = useState(null);
    const [rating, setRating] = useState(null);
    const [note, setNote] = useState(""); // Note state
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchMovieDetails = async () => {
            try {
                const response = await fetch(`https://localhost:7019/api/movie/${movieId}`);
                if (!response.ok) throw new Error("Failed to fetch movie details");
                const data = await response.json();
                setMovieData(data);
            } catch (err) {
                console.error(err);
                setError(err.message);
            }
        };

        const fetchUserRatingAndNote = async () => {
            try {
                const response = await fetch(`https://localhost:7019/api/rating/${movieId}`);
                if (response.ok) {
                    const data = await response.json();
                    setRating(data.rating);
                    setNote(data.note || ""); // Set existing note if available
                }
            } catch (err) {
                console.error(err);
            }
        };

        fetchMovieDetails();
        fetchUserRatingAndNote();
    }, [movieId]);

    const handleRatingChange = (e) => {
        const newRating = parseInt(e.target.value, 10);
        if (newRating >= 1 && newRating <= 10) {
            setRating(newRating);
        }
    };

    const submitRatingAndNote = async () => {
        try {
            const response = await fetch("https://localhost:7019/api/rating/rate", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    movieId,
                    rating,
                    note, // Include note in payload
                }),
            });

            if (!response.ok) throw new Error("Failed to submit rating and note");
            alert("Rating and note submitted successfully!");
        } catch (err) {
            console.error(err);
            setError(err.message);
        }
    };

    if (!movieData) return <p>Loading movie details...</p>;
    if (error) return <p className="error">{error}</p>;

    return (
        <div className="movie-detail-container">
            <h1>{movieData.title}</h1>
            <p>{movieData.description}</p>

            <div className="rating-section">
                <h3>Rate this Movie</h3>
                <input
                    type="number"
                    min="1"
                    max="10"
                    value={rating || ""}
                    onChange={handleRatingChange}
                />
            </div>

            <div className="comments-section">
                <h3>Add a Note</h3>
                <textarea
                    value={note}
                    onChange={(e) => setNote(e.target.value)}
                    placeholder="Write your thoughts about the movie..."
                />
            </div>

            <button onClick={submitRatingAndNote}>Submit</button>
        </div>
    );
};

export default MovieDetail;
