import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { UserContext } from "../App";
import "../CSS/Navbar.css";

const Navbar = ({ onLogout }) => {
  const [genres, setGenres] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedGenre, setSelectedGenre] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [searchCategory, setSearchCategory] = useState("general");
  const navigate = useNavigate();
  const { userId } = useContext(UserContext);

  useEffect(() => {
    const fetchGenres = async () => {
      try {
        const response = await fetch("https://localhost:7019/api/movie/genre");
        if (!response.ok) throw new Error("Failed to fetch genres");
        const data = await response.json();
        setGenres(data);
      } catch (error) {
        console.error("Error fetching genres:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchGenres();
  }, []);

  const handleGenreSelect = (genreName) => {
    setSelectedGenre(genreName);
    navigate(`/movies/genre/${genreName}`);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      navigate(`/search/${searchCategory}/${searchTerm.trim()}`);
      setSearchTerm("");
    }
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <h1 className="app-name" onClick={() => navigate("/")}>MovieApp</h1>
        <div>
          {loading ? (
            <p>Loading Genres...</p>
          ) : (
            <select
              className="dropdown-menu"
              value={selectedGenre}
              onChange={(e) => handleGenreSelect(e.target.value)}
            >
              <option value="">Select Genre</option>
              {genres.map((genre) => (
                <option key={genre.genreid} value={genre.genreName}>
                  {genre.genreName}
                </option>
              ))}
            </select>
          )}
        </div>
      </div>
      <div className="navbar-right">
        <form onSubmit={handleSearch} className="search-form">
          <select
            value={searchCategory}
            onChange={(e) => setSearchCategory(e.target.value)}
            className="search-category"
          >
            <option value="general">All</option>
            <option value="actorname">Actors</option>
            <option value="movie">Movies</option>
            <option value="releaseyear">Release Year</option>
          </select>
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          <button type="submit" className="search-button">Search</button>
        </form>
        <button onClick={onLogout} className="logout-button">Logout</button>
      </div>
    </nav>
  );
};

export default Navbar;
