import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Home from "./components/Home";
import AuthPage from "./components/AuthPage";
import Navbar from "./components/Navbar"; // Import Navbar
import RecentReleased from "./components/RecentReleased";
import MovieDetail from "./components/MovieDetail";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userId, setUserId] = useState(null);

  // Load userId from localStorage
  useEffect(() => {
    const storedUserId = localStorage.getItem("userId");
    if (storedUserId) {
      setIsAuthenticated(true);
      setUserId(storedUserId);
    }
  }, []);

  const handleAuthentication = (id) => {
    setIsAuthenticated(true);
    setUserId(id);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setUserId(null);
    localStorage.removeItem("userId");
  };

  return (
    <Router>
      {isAuthenticated && <Navbar onLogout={handleLogout} />}
      <Routes>
        <Route
          path="/auth"
          element={
            isAuthenticated ? (
              <Navigate to="/" />
            ) : (
              <AuthPage onAuthSuccess={handleAuthentication} />
            )
          }
        />
        <Route
          path="/"
          element={
            isAuthenticated ? (
              <Home userId={userId} onLogout={handleLogout} />
            ) : (
              <Navigate to="/auth" />
            )
          }
        />
        <Route
          path="/recentreleased"
          element={isAuthenticated ? <RecentReleased /> : <Navigate to="/auth" />}
        />
        <Route
          path="/movie/:tconst"
          element={isAuthenticated ? <MovieDetail /> : <Navigate to="/auth" />}
        />
        <Route path="*" element={<Navigate to="/auth" />} />
      </Routes>
    </Router>
  );
}

export default App;
