import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import AuthPage from "./AuthPage";
import Dashboard from "./Dashboard"; // Protected Component
import Home from "./Home"; // Public Component

const App = () => {
  const [isAuthorized, setIsAuthorized] = useState(false);

  useEffect(() => {
    // Check if user_id exists in sessionStorage
    const userId = sessionStorage.getItem("user_id");
    setIsAuthorized(!!userId); // Boolean: true if user_id exists
  }, []);

  const handleLogout = () => {
    sessionStorage.removeItem("user_id"); // Clear user_id
    setIsAuthorized(false);
  };

  return (
    <Router>
      <div>
        {isAuthorized ? (
          <button onClick={handleLogout}>Logout</button>
        ) : (
          <p>Please log in to access the application.</p>
        )}
      </div>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route
          path="/dashboard"
          element={isAuthorized ? <Dashboard /> : <Navigate to="/" />}
        />
        <Route path="/auth" element={<AuthPage />} />
      </Routes>
    </Router>
  );
};

export default App;
