// src/components/UserProfile.js
import React, { useEffect, useState, useContext } from "react";
import { UserContext } from "../App"; // Import UserContext
import "../CSS/UserProfile.css"; // Ensure CSS styling is present

const UserProfile = ({ onClose }) => {
  const { userId } = useContext(UserContext); // Access userId from UserContext
  const [userData, setUserData] = useState({ username: "", email: "" });
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        setLoading(true);

        // Fetch user information (username and email)
        const userInfoResponse = await fetch(
          `https://localhost:7019/api/User/userinfo/${userId}`
        );
        if (!userInfoResponse.ok) throw new Error("Failed to fetch user info.");
        const userInfoData = await userInfoResponse.json();

        // Fetch user notes
        const notesResponse = await fetch(
          `https://localhost:7019/api/Notes/notes/${userId}`
        );
        if (!notesResponse.ok) throw new Error("Failed to fetch user notes.");
        const notesData = await notesResponse.json();

        // Set state with fetched data
        setUserData(userInfoData);
        setNotes(notesData.items || notesData); // If paginated, access `items`
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    if (userId) fetchUserProfile();
  }, [userId]);

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="user-profile-popup">
      <div className="user-profile-content">
        <button className="close-btn" onClick={onClose}>
          X
        </button>
        <h2>User Profile</h2>
        <p>
          <strong>Username:</strong> {userData.username}
        </p>
        <p>
          <strong>Email:</strong> {userData.email}
        </p>
        <h3>Your Notes</h3>
        {notes.length > 0 ? (
          <ul className="notes-list">
            {notes.map((note, index) => (
              <li key={index}>
                <strong>Movie ID:</strong> {note.tconst} <br />
                <strong>Note:</strong> {note.note} <br />
                <strong>Created:</strong>{" "}
                {new Date(note.createdDate).toLocaleString()}
              </li>
            ))}
          </ul>
        ) : (
          <p>No notes found.</p>
        )}
      </div>
    </div>
  );
};

export default UserProfile;
