import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Home from './components/Home';
import AuthPage from './components/AuthPage';
import RecentReleased from './components/RecentReleased'; // Import RecentReleased component

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleAuthentication = () => {
    setIsAuthenticated(true);
  };

  return (
    <Router>
      <Routes>
        {/* Unified Auth Page */}
        <Route
          path="/auth"
          element={
            isAuthenticated ? (
              <Navigate to="/" />
            ) : (
              <AuthPage onAuthSuccess={handleAuthentication} />
            )
          }
        />

        {/* Protected Home Route */}
        <Route
          path="/"
          element={
            isAuthenticated ? <Home /> : <Navigate to="/auth" />
          }
        />

        {/* Protected Recent Released Route */}
        <Route
          path="/recent-released"
          element={
            isAuthenticated ? <RecentReleased /> : <Navigate to="/auth" />
          }
        />

        {/* Default Redirect */}
        <Route path="*" element={<Navigate to="/auth" />} />
      </Routes>
    </Router>
  );
}

export default App;
