import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import '../CSS/Navbar.css';

function Navbar({ onGenreSelect, searchTypes, onSearch }) {
  const [genres, setGenres] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedSearchType, setSelectedSearchType] = useState(searchTypes[0]);

  // Fetch genres on component mount
  useEffect(() => {
    const fetchGenres = async () => {
      try {
        const response = await fetch('https://localhost:7019/api/movie/genre');
        if (!response.ok) {
          throw new Error('Failed to fetch genres');
        }
        const data = await response.json();
        setGenres(data);
      } catch (error) {
        console.error('Error fetching genres:', error);
      }
    };

    fetchGenres();
  }, []);

  const handleGenreChange = (genre) => {
    setSelectedGenre(genre);
    onGenreSelect(genre);
  };

  const handleSearch = () => {
    onSearch({ searchType: selectedSearchType, query: searchQuery, genre: selectedGenre });
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <span className="app-name">MovieApp</span>
      </div>

      {/* Dropdown for Genres */}
      <div className="navbar-item">
        <select
          value={selectedGenre}
          onChange={(e) => handleGenreChange(e.target.value)}
        >
          <option value="">All Genres</option>
          {genres.map((genre) => (
            <option key={genre} value={genre}>
              {genre}
            </option>
          ))}
        </select>
      </div>

      {/* Links: Bookmarked & Recommended */}
      <div className="navbar-item">
        <Link to="/bookmarked">Bookmarked</Link>
      </div>
      <div className="navbar-item">
        <Link to="/recommended">Recommended</Link>
      </div>

      {/* Search Functionality */}
      <div className="navbar-item">
        <select
          value={selectedSearchType}
          onChange={(e) => setSelectedSearchType(e.target.value)}
        >
          {searchTypes.map((type) => (
            <option key={type} value={type}>
              {type}
            </option>
          ))}
        </select>
        <input
          type="text"
          placeholder="Search..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
    </nav>
  );
}

export default Navbar;
