import React, { useState, useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { UserContext } from '../App';
import '../CSS/MovieDetail.css';

const MovieDetail = () => {
  const { tconst } = useParams(); 
  const { userId } = useContext(UserContext); 
  const [movieDetails, setMovieDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [bookmarked, setBookmarked] = useState(false);
  const [rating, setRating] = useState(null);
  const [note, setNote] = useState("");

  useEffect(() => {
    const fetchMovieDetails = async () => {
      try {
        const response = await fetch(`https://localhost:7019/api/movie/${tconst}`);
        if (!response.ok) throw new Error("Failed to fetch movie details");
        const data = await response.json();
        setMovieDetails(data);
      } catch (err) {
        console.error(err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    const fetchUserInteractions = async () => {
      try {
        const response = await fetch(`https://localhost:7019/api/rating/${tconst}`);
        if (response.ok) {
          const data = await response.json();
          setRating(data.rating);
          setNote(data.note || "");
        }
      } catch (err) {
        console.error(err);
      }
    };

    fetchMovieDetails();
    fetchUserInteractions();
  }, [tconst]);

  const handleRatingChange = (e) => {
    const newRating = parseInt(e.target.value, 10);
    if (newRating >= 1 && newRating <= 10) {
      setRating(newRating);
    }
  };

  const handleBookmarkToggle = async () => {
    try {
      const response = await fetch(`https://localhost:7019/api/bookmark/${tconst}`, {
        method: bookmarked ? 'DELETE' : 'POST',
      });
      if (response.ok) {
        setBookmarked(!bookmarked);
      } else {
        throw new Error("Failed to update bookmark status");
      }
    } catch (err) {
      console.error(err);
      setError(err.message);
    }
  };

  const submitRatingAndNote = async () => {
    try {
      const response = await fetch("https://localhost:7019/api/rating/rate", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          movieId: tconst,
          userId,
          rating,
          note,
        }),
      });

      if (!response.ok) throw new Error("Failed to submit rating and note");
      alert("Rating and note submitted successfully!");
    } catch (err) {
      console.error(err);
      setError(err.message);
    }
  };

  if (loading) return <p>Loading movie details...</p>;
  if (error) return <p className="error">{error}</p>;

  return (
    <div className="movie-detail-container">
      <h1>{movieDetails.title}</h1>
      <p>{movieDetails.description}</p>
      <button onClick={handleBookmarkToggle}>
        {bookmarked ? "Remove Bookmark" : "Add Bookmark"}
      </button>
      <div className="rating-section">
        <h3>Rate this Movie</h3>
        <input
          type="number"
          min="1"
          max="10"
          value={rating || ""}
          onChange={handleRatingChange}
        />
      </div>
      <div className="comments-section">
        <h3>Add a Note</h3>
        <textarea
          value={note}
          onChange={(e) => setNote(e.target.value)}
          placeholder="Write your thoughts about the movie..."
        />
      </div>
      <button onClick={submitRatingAndNote}>Submit</button>
    </div>
  );
};

export default MovieDetail;
