import React, { useState, useEffect } from 'react';
import '../CSS/Navbar.css';

const Navbar = () => {
  const [genres, setGenres] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState('All Genres');
  const [searchQuery, setSearchQuery] = useState('');
  const [searchType, setSearchType] = useState('Movies');
  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState(null); // Error state

  // Fetch genres on component mount
  useEffect(() => {
    const fetchGenres = async () => {
      try {
        setLoading(true);
        const response = await fetch('https://localhost:7019/api/movie/genre');
        if (!response.ok) {
          throw new Error('Failed to fetch genres');
        }
        const data = await response.json();
        setGenres(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchGenres();
  }, []);

  const handleSearch = () => {
    if (!searchQuery.trim()) return;
    console.log(`Searching for ${searchType}: ${searchQuery}`);
    // Implement search functionality
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <h1 className="app-name">MovieApp</h1>
        <select
          className="dropdown-menu"
          value={selectedGenre}
          onChange={(e) => setSelectedGenre(e.target.value)}
        >
          {loading && <option>Loading...</option>}
          {error && <option>Error: {error}</option>}
          {!loading &&
            !error &&
            genres.map((genre) => (
              <option key={genre.genreid} value={genre.genrename}>
                {genre.genrename}
              </option>
            ))}
        </select>
        <button className="navbar-link">Recently Added</button>
        <button className="navbar-link">Bookmarked</button>
        <button className="navbar-link">Recommended</button>
      </div>
      <div className="navbar-center">
        <button className="navbar-user-profile">User Profile</button>
      </div>
      <div className="navbar-right">
        <select
          className="dropdown-menu"
          value={searchType}
          onChange={(e) => setSearchType(e.target.value)}
        >
          <option value="Movies">Movies</option>
          <option value="Actors">Actors</option>
          <option value="Directors">Directors</option>
        </select>
        <input
          type="text"
          className="search-bar"
          placeholder={`Search ${searchType}...`}
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <button className="search-button" onClick={handleSearch}>
          Search
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
