import React, { useState } from 'react';
import '../CSS/Navbar.css';
import UserProfile from './UserProfile'; // Import UserProfile component

const Navbar = ({ onLogout }) => {
  const [isProfileOpen, setIsProfileOpen] = useState(false);

  const toggleProfile = () => {
    setIsProfileOpen(!isProfileOpen);
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <h1 className="app-name">MovieApp</h1>
        <select className="dropdown-menu">
          <option value="All Genres">All Genres</option>
          <option value="Action">Action</option>
          <option value="Comedy">Comedy</option>
          <option value="Drama">Drama</option>
        </select>
        <button className="navbar-link">Recently Added</button>
        <button className="navbar-link">Bookmarked</button>
        <button className="navbar-link">Recommended</button>
      </div>
      <div className="navbar-center">
        <button className="navbar-user-profile" onClick={toggleProfile}>
          User Profile
        </button>
      </div>
      <div className="navbar-right">
        <select className="dropdown-menu">
          <option value="Movies">Movies</option>
          <option value="Actors">Actors</option>
          <option value="Directors">Directors</option>
        </select>
        <input
          type="text"
          className="search-bar"
          placeholder="Search..."
        />
        <button className="search-button">Search</button>
      </div>
      {isProfileOpen && <UserProfile onClose={toggleProfile} onLogout={onLogout} />}
    </nav>
  );
};

export default Navbar;
