// src/components/UserProfile.js
import React, { useEffect, useState, useContext } from "react";
import { UserContext } from "../App";
import "../CSS/UserProfile.css";

const UserProfile = ({ onClose }) => {
  const { userId } = useContext(UserContext); // Get userId from context
  const [userDetails, setUserDetails] = useState({ username: "", email: "" });
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        // Fetch user details
        const userResponse = await fetch(
          `https://localhost:7019/api/User/userinfo/${userId}`
        );
        if (!userResponse.ok) throw new Error("Failed to fetch user details");

        const userData = await userResponse.json();

        // Fetch user notes
        const notesResponse = await fetch(
          `https://localhost:7019/api/Notes/notes/${userId}`
        );
        if (!notesResponse.ok) throw new Error("Failed to fetch user notes");

        const notesData = await notesResponse.json();

        // Update states
        setUserDetails(userData);
        setNotes(notesData.items || []); // Ensure pagination is handled
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchUserProfile();
  }, [userId]);

  if (loading) return <div className="user-profile-loading">Loading...</div>;
  if (error) return <div className="user-profile-error">{error}</div>;

  return (
    <div className="user-profile-overlay">
      <div className="user-profile-popup">
        <button className="close-button" onClick={onClose}>
          &times;
        </button>
        <h2>User Profile</h2>
        <p>
          <strong>Username:</strong> {userDetails.Username}
        </p>
        <p>
          <strong>Email:</strong> {userDetails.Email}
        </p>
        <h3>Notes</h3>
        <ul>
          {notes.length > 0 ? (
            notes.map((note, index) => (
              <li key={index}>
                <strong>Movie ID:</strong> {note.tconst} |{" "}
                <strong>Note:</strong> {note.note}
              </li>
            ))
          ) : (
            <p>No notes available</p>
          )}
        </ul>
      </div>
    </div>
  );
};

export default UserProfile;
