import React, { useState, useEffect } from 'react';
import '../CSS/Navbar.css';

const Navbar = () => {
  const [genres, setGenres] = useState([]);
  const [genre, setGenre] = useState('All Genres');
  const [searchType, setSearchType] = useState('Movies');
  const [searchQuery, setSearchQuery] = useState('');
  const [error, setError] = useState('');

  // Fetch genres from the backend API
  useEffect(() => {
    const fetchGenres = async () => {
      try {
        const response = await fetch('https://localhost:7019/api/api/movie/genre/{Genre}'); // Replace with actual endpoint
        if (!response.ok) throw new Error('Failed to fetch genres');
        const data = await response.json();
        setGenres(data);
      } catch (err) {
        setError('Error fetching genres');
      }
    };

    fetchGenres();
  }, []);

  const handleSearch = () => {
    if (!searchQuery.trim()) return;
    console.log(`Searching for ${searchType}: ${searchQuery}`);
    // Implement search functionality
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <h1 className="app-name">MovieApp</h1>
        <select
          className="dropdown-menu"
          value={genre}
          onChange={(e) => setGenre(e.target.value)}
        >
          <option value="All Genres">All Genres</option>
          {genres.map((g) => (
            <option key={g.id} value={g.name}>
              {g.name}
            </option>
          ))}
        </select>
        <button className="navbar-link">Recently Added</button>
        <button className="navbar-link">Bookmarked</button>
        <button className="navbar-link">Recommended</button>
      </div>
      <div className="navbar-center">
        <button className="navbar-user-profile">User Profile</button>
      </div>
      <div className="navbar-right">
        <select
          className="dropdown-menu"
          value={searchType}
          onChange={(e) => setSearchType(e.target.value)}
        >
          <option value="Movies">Movies</option>
          <option value="Actors">Actors</option>
          <option value="Directors">Directors</option>
        </select>
        <input
          type="text"
          className="search-bar"
          placeholder={`Search ${searchType}...`}
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <button className="search-button" onClick={handleSearch}>
          Search
        </button>
      </div>
      {error && <p className="error-message">{error}</p>}
    </nav>
  );
};

export default Navbar;
