import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Home from './components/Home';
import AuthPage from './components/AuthPage';
import Navbar from './components/Navbar';
import GenrebasedMovies from './components/GenrebasedMovies';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleAuthentication = () => {
    setIsAuthenticated(true);
  };

  // Define genres and search types
  const genres = ['Action', 'Comedy', 'Drama', 'Horror', 'Sci-Fi'];
  const searchTypes = ['Title', 'Director', 'Actor'];

  return (
    <Router>
      {/* Pass both genres and searchTypes to Navbar */}
      {isAuthenticated && <Navbar genres={genres} searchTypes={searchTypes} />}
      <Routes>
        <Route
          path="/auth"
          element={
            isAuthenticated ? (
              <Navigate to="/" />
            ) : (
              <AuthPage onAuthSuccess={handleAuthentication} />
            )
          }
        />
        <Route
          path="/"
          element={
            isAuthenticated ? <Home /> : <Navigate to="/auth" />
          }
        />
        <Route
          path="/movies/genre/:genreName"
          element={
            isAuthenticated ? <GenrebasedMovies /> : <Navigate to="/auth" />
          }
        />
        <Route path="*" element={<Navigate to="/auth" />} />
      </Routes>
    </Router>
  );
}

export default App;
