import React, { useEffect, useState } from 'react';
import '../CSS/UserProfile.css';

const UserProfile = ({ onClose, onLogout }) => {
  const [userDetails, setUserDetails] = useState(null);
  const [userNotes, setUserNotes] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        // Fetch user details
        const userResponse = await fetch('https://localhost:7019/api/User/{userId}/details?page=1&pageSize=10');
        if (!userResponse.ok) throw new Error('Failed to fetch user details');
        const userData = await userResponse.json();
        setUserDetails(userData);

        // Fetch user notes
        const notesResponse = await fetch(`https://localhost:7019/api/User/${userData.username}/notes?page=1&pageSize=10`);
        if (!notesResponse.ok) throw new Error('Failed to fetch user notes');
        const notesData = await notesResponse.json();
        setUserNotes(notesData.notes);
      } catch (err) {
        setError(err.message);
      }
    };

    fetchUserData();
  }, []);

  const handleLogout = () => {
    onLogout();
  };

  return (
    <div className="user-profile-popup">
      <div className="user-profile-header">
        <h2>User Profile</h2>
        <button className="close-button" onClick={onClose}>
          X
        </button>
      </div>
      {error && <p className="error-message">{error}</p>}
      {userDetails ? (
        <>
          <div className="user-info">
            <p><strong>Username:</strong> {userDetails.username}</p>
            <p><strong>Email:</strong> {userDetails.email}</p>
          </div>
          <div className="user-notes">
            <h3>Your Notes</h3>
            <ul>
              {userNotes.map((note) => (
                <li key={note.Tconst}>{note.Note}</li>
              ))}
            </ul>
          </div>
        </>
      ) : (
        <p>Loading user details...</p>
      )}
      <button className="logout-button" onClick={handleLogout}>
        Log Out
      </button>
    </div>
  );
};

export default UserProfile;
