import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Home from './components/Home';
import AuthPage from './components/AuthPage';
import NavigationBar from './components/Navbar';
//import RecentReleased from './components/RecentReleased'; // Placeholder
import MovieDetail from './components/MovieDetail'; // Placeholder

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleAuthentication = () => {
    setIsAuthenticated(true);
  };

  return (
    <Router>
      {isAuthenticated && <NavigationBar />}
      <Routes>
        {/* Unified Auth Page */}
        <Route
          path="/auth"
          element={
            isAuthenticated ? (
              <Navigate to="/" />
            ) : (
              <AuthPage onAuthSuccess={handleAuthentication} />
            )
          }
        />

        {/* Protected Routes */}
        <Route
          path="/"
          element={isAuthenticated ? <Home /> : <Navigate to="/auth" />}
        />
        <Route
          path="/recentreleased"
          element={isAuthenticated ? <RecentReleased /> : <Navigate to="/auth" />}
        />
        <Route
          path="/movies/:genre"
          element={isAuthenticated ? <MovieDetail /> : <Navigate to="/auth" />}
        />

        {/* Default Redirect */}
        <Route path="*" element={<Navigate to="/auth" />} />
      </Routes>
    </Router>
  );
}

export default App;
