import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../CSS/Navbar.css';

function Navbar({ genres, searchTypes }) {
  const navigate = useNavigate();
  const [selectedGenre, setSelectedGenre] = useState('');
  const [selectedSearchType, setSelectedSearchType] = useState(searchTypes[0]);
  const [searchQuery, setSearchQuery] = useState('');

  const handleGenreChange = (genre) => {
    if (genre) {
      navigate(`/movies/genre/${genre}`);
    }
  };

  const handleSearch = () => {
    if (searchQuery.trim()) {
      navigate(`/search?type=${selectedSearchType}&query=${searchQuery}`);
    }
  };

  return (
    <nav className="navbar">
      {/* Application Name */}
      <div className="navbar-left">
        <span className="app-name">MovieApp</span>
      </div>

      {/* Dropdown for Genres */}
      <div className="navbar-item">
        <select value={selectedGenre} onChange={(e) => handleGenreChange(e.target.value)}>
          <option value="">All Genres</option>
          {genres.map((genre) => (
            <option key={genre} value={genre}>
              {genre}
            </option>
          ))}
        </select>
      </div>

      {/* Links: Bookmarked & Recommended */}
      <div className="navbar-item">
        <button onClick={() => navigate('/bookmarked')}>Bookmarked</button>
      </div>
      <div className="navbar-item">
        <button onClick={() => navigate('/recommended')}>Recommended</button>
      </div>

      {/* User Profile (Centered) */}
      <div className="navbar-center">
        <span className="user-profile">User Profile</span>
      </div>

      {/* Dropdown for Search Type */}
      <div className="navbar-item">
        <select
          value={selectedSearchType}
          onChange={(e) => setSelectedSearchType(e.target.value)}
        >
          {searchTypes.map((type) => (
            <option key={type} value={type}>
              {type}
            </option>
          ))}
        </select>
      </div>

      {/* Search Bar */}
      <div className="navbar-item">
        <input
          type="text"
          placeholder="Search..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
    </nav>
  );
}

export default Navbar;
