import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../CSS/Navbar.css'; // Custom CSS for styling

function Navbar({ onSearch, genres, searchTypes }) {
  const [selectedGenre, setSelectedGenre] = useState('');
  const [selectedSearchType, setSelectedSearchType] = useState(searchTypes[0]);
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearch = () => {
    onSearch({ searchType: selectedSearchType, query: searchQuery, genre: selectedGenre });
  };

  return (
    <nav className="navbar">
      {/* Application Name */}
      <div className="navbar-left">
        <span className="app-name">MovieApp</span>
      </div>

      {/* Dropdown for Genres */}
      <div className="navbar-item">
        <select value={selectedGenre} onChange={(e) => setSelectedGenre(e.target.value)}>
          <option value="">All Genres</option>
          {genres.map((genre) => (
            <option key={genre} value={genre}>
              {genre}
            </option>
          ))}
        </select>
      </div>

      {/* Links: Bookmarked & Recommended */}
      <div className="navbar-item">
        <Link to="/bookmarked">Bookmarked</Link>
      </div>
      <div className="navbar-item">
        <Link to="/recommended">Recommended</Link>
      </div>

      {/* User Profile (Centered) */}
      <div className="navbar-center">
        <span className="user-profile">User Profile</span>
      </div>

      {/* Dropdown for Search Type */}
      <div className="navbar-item">
        <select
          value={selectedSearchType}
          onChange={(e) => setSelectedSearchType(e.target.value)}
        >
          {searchTypes.map((type) => (
            <option key={type} value={type}>
              {type}
            </option>
          ))}
        </select>
      </div>

      {/* Search Bar */}
      <div className="navbar-item">
        <input
          type="text"
          placeholder="Search..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
    </nav>
  );
}

export default Navbar;
