import React, { useState, useEffect } from "react";

const MovieDetail = ({ movieId }) => {
    const [movieData, setMovieData] = useState(null); // Movie details
    const [rating, setRating] = useState(null); // User rating
    const [error, setError] = useState(null);

    useEffect(() => {
        // Fetch movie details
        const fetchMovieDetails = async () => {
            try {
                const response = await fetch(`https://localhost:7019/api/movie/${movieId}`);
                if (!response.ok) throw new Error("Failed to fetch movie details");
                const data = await response.json();
                setMovieData(data);
            } catch (err) {
                console.error(err);
                setError(err.message);
            }
        };

        // Fetch user rating
        const fetchUserRating = async () => {
            try {
                const response = await fetch(`https://localhost:7019/api/rating/${movieId}`);
                if (response.ok) {
                    const data = await response.json();
                    setRating(data.rating); // Set existing rating if available
                }
            } catch (err) {
                console.error(err);
            }
        };

        fetchMovieDetails();
        fetchUserRating();
    }, [movieId]);

    const handleRatingChange = (e) => {
        const newRating = parseInt(e.target.value, 10);
        if (newRating >= 1 && newRating <= 10) {
            setRating(newRating);
        }
    };

    const submitRating = async () => {
        try {
            const response = await fetch("https://localhost:7019/api/rating/rate", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    movieId,
                    rating,
                }),
            });

            if (!response.ok) throw new Error("Failed to submit rating");

            alert("Rating submitted successfully!");
        } catch (err) {
            console.error(err);
            setError(err.message);
        }
    };

    if (!movieData) return <p>Loading movie details...</p>;
    if (error) return <p style={{ color: "red" }}>{error}</p>;

    return (
        <div>
            <h1>{movieData.title}</h1>
            <p>{movieData.description}</p>
            <h3>Rate this Movie</h3>
            <input
                type="number"
                min="1"
                max="10"
                value={rating || ""}
                onChange={handleRatingChange}
            />
            <button onClick={submitRating}>Submit Rating</button>
        </div>
    );
};

export default MovieDetail;
