import React, { useState, useEffect } from 'react';
import '../CSS/UserProfile.css';

const UserProfile = ({ onLogOff }) => {
  const [userData, setUserData] = useState({ username: '', email: '' });
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        setIsLoading(true);
        const userId = localStorage.getItem('userId'); // Assuming userId is stored after login
        const response = await fetch(`/api/User/details/${userId}`);
        if (!response.ok) {
          throw new Error('Failed to fetch user details');
        }
        const data = await response.json();
        setUserData({ username: data.username, email: data.email });
      } catch (err) {
        setError(err.message);
      } finally {
        setIsLoading(false);
      }
    };

    fetchUserDetails();
  }, []);

  return (
    <div className="user-profile-popup">
      <div className="popup-content">
        {isLoading ? (
          <p>Loading...</p>
        ) : error ? (
          <p className="error">{error}</p>
        ) : (
          <>
            <h2>User Profile</h2>
            <p><strong>Username:</strong> {userData.username}</p>
            <p><strong>Email:</strong> {userData.email}</p>
            <button className="log-off-button" onClick={onLogOff}>
              Log Off
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default UserProfile;
