import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { UserContext } from "../App";
import UserProfile from "./UserProfile";
import "../CSS/Navbar.css";

const Navbar = ({ onLogout }) => {
  const [showUserProfile, setShowUserProfile] = useState(false);
  const [genres, setGenres] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const { userId } = useContext(UserContext);

  useEffect(() => {
    const fetchGenres = async () => {
      try {
        const response = await fetch("https://localhost:7019/api/movie/genre");
        if (!response.ok) throw new Error("Failed to fetch genres");

        const data = await response.json();
        setGenres(data);
      } catch (error) {
        console.error("Error fetching genres:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchGenres();
  }, []);

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <h1 className="app-name" onClick={() => navigate("/")}>
          MovieApp
        </h1>
        <button onClick={() => navigate("/recentreleased")} className="navbar-link">
          Recent Releases
        </button>
      </div>

      <div className="navbar-right">
        <button className="navbar-link" onClick={() => setShowUserProfile(true)}>
          User Profile
        </button>
        <button onClick={onLogout} className="logout-button">
          Logout
        </button>
      </div>

      {/* User Profile Popup */}
      {showUserProfile && (
        <UserProfile onClose={() => setShowUserProfile(false)} />
      )}
    </nav>
  );
};

export default Navbar;
