import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { UserContext } from "../App"; // Import UserContext
import "../CSS/Navbar.css";

const Navbar = ({ onLogout }) => {
  const [genres, setGenres] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedGenre, setSelectedGenre] = useState("");
  const navigate = useNavigate();

  const { userId } = useContext(UserContext); // Access userId from context

  useEffect(() => {
    const fetchGenres = async () => {
      try {
        const response = await fetch("https://localhost:7019/api/movie/genre");
        if (!response.ok) throw new Error("Failed to fetch genres");

        const data = await response.json();
        setGenres(data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching genres:", error);
      }
    };

    fetchGenres();
  }, []);

  const handleGenreSelect = (genreName) => {
    setSelectedGenre(genreName);
    navigate(`/movies/genre/${genreName}`);
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <h1 className="app-name" onClick={() => navigate("/")}>
          MovieApp
        </h1>
        <div>
          {loading ? (
            <p>Loading Genres...</p>
          ) : (
            <select
              className="dropdown-menu"
              value={selectedGenre}
              onChange={(e) => handleGenreSelect(e.target.value)}
            >
              <option value="">Select Genre</option>
              {genres.map((genre) => (
                <option key={genre.genreid} value={genre.genreName}>
                  {genre.genreName}
                </option>
              ))}
            </select>
          )}
        </div>
        <button onClick={() => navigate("/recentreleased")} className="navbar-link">
          Recent Releases
        </button>
        <button onClick={() => navigate("/bookmarks")} className="navbar-link">
          Bookmarked
        </button>
        <button onClick={() => navigate("/recommended")} className="navbar-link">
          Recommended
        </button>
      </div>

      <div className="navbar-right">
        <p>User ID: {userId}</p> {/* Display User ID */}
        <button onClick={onLogout} className="logout-button">
          Logout
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
